# -*- coding: utf-8 -*-
import base64, struct
bmp_data = base64.b64decode('')

def bmp_info(data):
    bmp_header=struct.unpack('<2c6I2H',data[0:30])
    data_is_bmp=bmp_header[0]+bmp_header[1]==b'BM'
    bmp_body=struct.unpack('<%ss'%(bmp_header[2]-30),data[30:])
    if  data_is_bmp:
        return {
            'width': bmp_header[6],
            'height': bmp_header[7],
            'color': bmp_header[9],
            'body':bmp_body
        }
    else:
        pass

# 测试
bi = bmp_info(bmp_data)
assert bi['width'] == 28
assert bi['height'] == 10
assert bi['color'] == 16
print('ok')